#1#Combien y a-t-il de journalistes dans ce array ? 
def aff_nb_handle(tab)
	puts "Au total il y a #{tab.count} journalistes"
	return tab.length
end

#2#Combien d'handle contiennent un numéro ?
def is_there_a_numb(liste)
	mots = []
	mots << "liste"
	mn = 0
	for i in 0...liste.size
		for j in 0...liste[i].size
			if liste[i][j].to_i != 0
				exist = true
				break
			else
				exist = false
			end
		end
		if exist 
			mn += 1
		end
	end
	puts "Au total il y a #{mn} d'handle contennant un numero"
end

#3#Combien d'handle contiennent les 4 lettres du prénom "Aude" à la suite (sans prendre en compte les majuscules) ?
def howmany_4lAude(liste)
	total = liste.grep(/[Aa]ude/)
	puts "Il y a au total #{total.length} handle(s) contennant le prenom 'Aude'"
end

#4#Combien commencent par une majuscule (première lettre juste après le @) ?
def dmaj(liste)
	deb_maj = []
	for i in 1...(liste.length)
		if liste[i][1] == 'Q'|| liste[i][1] == 'W' || liste[i][1] == 'E'||liste[i][1] == 'R'||liste[i][1] == 'T'||liste[i][1] == 'Y'||liste[i][1] == 'U'||liste[i][1] == 'I'||liste[i][1] == 'O'||liste[i][1] == 'P'||liste[i][1] == 'A'||liste[i][1] == 'S'||liste[i][1] == 'D'||liste[i][1] == 'F'||liste[i][1] == 'G'||liste[i][1] == 'H'||liste[i][1] == 'J'||liste[i][1] == 'K'||liste[i][1] == 'L'||liste[i][1] == 'Z'||liste[i][1] == 'X'||liste[i][1] == 'C'||liste[i][1] == 'V'||liste[i][1] == 'B'||liste[i][1] == 'N'||liste[i][1] == 'M'
		deb_maj << liste[i][1]  
		end
	end 

	puts "Il y a #{deb_maj.length} handle(s) avec Un majuscule au debut"
end

#5.1#Combien contient au moins une majuscule?
def maj1(liste)
	lmaj = liste.select {|index| index =~ /[A-Z]/}
	puts "Au total il y a #{lmaj.length} handle(s) avec au moins une majuscule"
end

#5.2#Combien contiennent seulement une majuscule?
def maj2(liste)
	total = 0
	for mot in liste
		if mot.scan(/[A-Z]/).length == 1
			total += 1
		end
	end
	puts "Il y a au total #{total} handle avec seulement une majuscule"
end

#6#Combien y a-t-il de underscore _ dans tous les pseudos confondus ?
def nb_underscore(liste)
	filtre_tab = []
	for i in 0...(liste.length)
		word = liste[i]
		exist = false
		for j in 0...(word.length)
			if word[j] == '_'
				exist = true
				filtre_tab << word
			end
		end
	end
	puts filtre_tab.length
end

#7#Trie la liste de handle par ordre alphabétique.
def trier(liste)
	liste = liste.each {|mot| mot.downcase!}
	puts liste.sort
end

#8#Quels sont les 50 handles les plus courts de ce array ?
def court(liste)
	court = liste.sort_by(&:length)
	puts court[0..49]
end

#9#Quelle est la position dans l'array de la personne @epenser ?
def pos(liste, handle)
	puts "Entrez le Handle dont tu veux voir la position"
	print "> "
	handle = gets.chomp
	puts liste.index(handle) 
end

